import React from 'react'
import EventButtonCampoo from '../components/EventButtonCampoo';
import { ScrollView, TouchableOpacity, StyleSheet, SafeAreaView, StatusBar, View, Image, Text, Platform } from 'react-native';
import EventButtonCampooLight from '../components/EventButtonLightCampoo';
import { CAMPOO } from '../../assets/themes/ThemeCampoo';
import EventUserItem from '../components/EventUserItem';


export default class EventUserCampoo extends React.Component {

    render() {

        return (

            <SafeAreaView style={styles.container}>
                <View style={styles.headerContainer}>

                    <TouchableOpacity style={styles.mainButton}>
                        <EventButtonCampoo >
                            <Text style={styles.titleButton1}>Evenement du campus</Text>

                        </EventButtonCampoo>

                    </TouchableOpacity>


                    <TouchableOpacity style={styles.userButton}>
                        <EventButtonCampoo >
                            <Text style={styles.titleButton2} >Tes evenements</Text>
                        </EventButtonCampoo>
                    </TouchableOpacity>



                </View>
                <ScrollView style={styles.scrollContainer} showsVerticalScrollIndicator={false}>

                    <View style={styles.eventContainer}>

                    </View>

                    <EventUserItem
                        image={require("../../assets/images/imagePost-Test.png")}
                        title="TITRE DE L'EVENEMENT"

                        eventText="Description de l’evenement Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad "

                        date='17/05/2020'

                        hours='18h20'

                        location='2 rue ta soeur tire au flanc'

                    />


                    <EventUserItem
                        image={require("../../assets/images/imagePost-Test.png")}

                        title="TITRE DE L'EVENEMENT"

                        eventText="Description de l’evenement Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad "

                        date='17/05/2020'

                        hours='18h20'

                        location='2 rue ta soeur tire au flanc' />

                    <EventUserItem
                        image={require("../../assets/images/imagePost-Test.png")}

                        title="TITRE DE L'EVENEMENT"

                        eventText="Description de l’evenement Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad "

                        date='17/05/2020'

                        hours='18h20'

                        location='2 rue ta soeur tire au flanc' />

                </ScrollView>



            </SafeAreaView>

        );




    }



}

const styles = StyleSheet.create({

    container: {
        flex: 1,
        //  pour detecter la platform
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',


    },
    userButton: {
        backgroundColor: CAMPOO.primary,
        flex: 0,
        flexDirection: 'row',


        width: '45%',
        justifyContent: 'center',
        alignItems: 'center',


        borderRadius: 5,

        height: 50,


    },
    titleButton2: {
        height: 50,

        fontSize: 16,
        color: 'white',



    },

    mainButton: {
        backgroundColor: CAMPOO.secondary,
        flex: 0,
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        width: '45%',
        borderRadius: 0,
        height: 50,
        paddingHorizontal: 40,
        textAlign: 'center',



    },
    titleButton1: {

        height: 50,
        fontSize: 15,
        color: CAMPOO.primary,
    },

    scrollContainer: {
        width: '90%',
    },

    eventContainer: {
        width: '100%',


    },


    headerContainer: {
        width: '90%',
        height: 60,
        flex: 0,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',






    },








})
