'use strict';

import React from 'react';

import { StyleSheet, Image, StatusBar, TouchableOpacity, SafeAreaView, View, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import SettingsSvg from '../../components/SettingsSvg';
import PenSvg from '../../components/PenSvg';
import Tags from '../../components/Tags';
import { CAMPOO } from '../../../assets/themes/ThemeCampoo';




export default function UserProfil({ navigation }) {


    return (

        <SafeAreaView style={styles.container}>
            {/* Contenaire  */}
            <View style={styles.profilIdContainer}>

                <View style={[styles.profilSettings, styles.gap]}>



                    {/* Profil de l'image */}
                    <Image source={require("../../../assets/imgProfil/Badeaubleu.png")} style={styles.profilPics}></Image>

                    <TouchableOpacity style={[styles.profilReglage]} onPress={() => navigation.navigate('SettingStackScreen')}>
                        <SettingsSvg />
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.profilModif]} onPress={() => navigation.navigate('UserModificationPage')}>
                        <PenSvg />
                    </TouchableOpacity>
                </View>

            </View>

            {/* Le pseudo et nom de l'user */}
            <View style={styles.profilInfoContainer}>
                <Text style={[styles.Pname, styles.colorViolet]}> Entoinee</Text>

                <Text style={[styles.nickName, styles.colorViolet]}> LeBgDu667</Text>

            </View>

            {/* l'
               icone de son batiment et la description du profil */}
            <View style={styles.BioContainer}>
                <Image source={require("../../../assets/imgProfil/esieeIcon.png")} style={[styles.iconsBat, styles.gap]} ></Image>
                <Text style={[styles.bioBloc, styles.colorViolet]} >Nouvelle musique disponible sur toutes les platformes allez check  Nouvelle musique disponible sur toutes les platformes allez check Nouvelle musique disponible sur toutes les platformes allez check</Text>
            </View>


            <Text style={[styles.colorViolet, styles.tagTitle]}>Centre d'intérêts:</Text>

            {/* les tags figurants sur le profil utilisateur */}

            <View style={styles.tagsContainer}>
                <View style={styles.tagsContent}>

                    <Tags>Musique</Tags>
                    <Tags>Cinema</Tags>
                    <Tags>Restaux</Tags>



                    <Tags>Livres</Tags>
                    <Tags>Musées</Tags>
                    <Tags>Natation</Tags>

                    <Tags>Musique</Tags>
                    <Tags>Cinema</Tags>
                    <Tags>Restaux</Tags>



                    <Tags>Livres</Tags>
                    <Tags>Musées</Tags>
                    <Tags>Natation</Tags>

                </View>

            </View>

        </SafeAreaView>




    );

};

const styles = StyleSheet.create({

    container: {
        flex: 1,
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        position: 'relative',
        flexDirection: 'column',
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    colorViolet: {
        color: CAMPOO.primary,
    },

    profilIdContainer: {
        flex: 0,
        height: '20%',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'white',

    },

    profilInfoContainer: {
        flex: 0,
        height: 'auto',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',

    },

    profilSettings: {
        flex: 1,
        width: '100%',
        position: 'relative',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'row',

    },

    profilPics: {
        width: 130,
        height: 130,
        borderRadius: 130 / 2,
    },
    profilReglage: {
        width: 25,
        height: 25,
        position: 'absolute',
        right: 75,

    },

    profilModif: {
        width: 25,
        height: 25,
        position: 'absolute',
        right: 30,

    },

    Pname: {
        fontSize: 16,
        marginBottom: 10,
    },

    nickName: {
        fontSize: 30,
        fontWeight: 'bold',
    },

    BioContainer: {
        flex: 0.3,
        flexDirection: 'row',
        height: 'auto',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        borderBottomWidth: 1,
        paddingBottom: '3%',
        borderColor: 'rgba(158, 150, 150, .5)',

    },
    iconsBat: {
        flex: 0,
        alignSelf: 'center',
        marginLeft: 10,
        width: 75,
        height: 65,

    },
    bioBloc: {

        width: '80%',
        height: 'auto',
        alignSelf: 'center',
        fontSize: 14,
        marginLeft: 25,
        marginTop: 15,
        textAlign: 'justify',
        flexShrink: 1,



    },
    tagsContainer: {
        flex: 1,
        width: '100%',
        // marginTop:5,





    },
    tagsContent: {
        flex: 1,
        width: '100%',
        marginBottom: 25,
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'center',
        alignItems: 'center',

    },
    tagTitle: {
        height: 30,
        fontSize: 17,
        fontWeight: 'bold',
        marginLeft: 20,
        marginTop: 18,
        width: '100%',

    }



});
