    DataHeure: {
        marginTop: 1,
        fontSize: 20,


    },

    like_Button: {
        width: 36,
        height: 36,
        marginTop: 10,


    },

    Meta_like: {
        height: 41,
        width: 323,
        flex: 1,
        flexDirection: 'row',
        justifyContent: "space-between",




    }









})
